import{_ as e,o as a,c as s,a as n}from"./app.e1e4c4ed.js";const o={},t=n(`<p>Linux\u4F5C\u4E3A\u4E00\u4E2A\u5F00\u6E90\u7684\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u5DF2\u7ECF\u6210\u4E3A\u8BA1\u7B97\u9886\u57DF\u7684\u7470\u5B9D\u3002\u4ECE\u670D\u52A1\u5668\u5230\u4E2A\u4EBA\u7535\u8111\uFF0C\u518D\u5230\u5D4C\u5165\u5F0F\u7CFB\u7EDF\uFF0CLinux\u7684\u5E7F\u6CDB\u5E94\u7528\u548C\u7075\u6D3B\u6027\u4F7F\u5176\u5728\u5F53\u4ECA\u7684\u6570\u5B57\u65F6\u4EE3\u4E2D\u4E0D\u53EF\u6216\u7F3A\u3002</p><hr><h2 id="linux\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#linux\u57FA\u7840" aria-hidden="true">#</a> Linux\u57FA\u7840</h2><p>Linux \u662F\u4E00\u79CD\u5F00\u6E90\u64CD\u4F5C\u7CFB\u7EDF\u5185\u6838\uFF0C\u5B83\u662F\u57FA\u4E8E\u7C7B Unix \u64CD\u4F5C\u7CFB\u7EDF\u7684\u4E00\u4E2A\u91CD\u8981\u7EC4\u6210\u90E8\u5206\u3002Linux \u5185\u6838\u6700\u521D\u7531\u82AC\u5170\u7A0B\u5E8F\u5458 Linus Torvalds \u57281991\u5E74\u521B\u5EFA\uFF0C\u5E76\u5728\u4E4B\u540E\u7531\u5168\u7403\u5404\u5730\u7684\u5F00\u53D1\u8005\u548C\u793E\u533A\u4E0D\u65AD\u5F00\u53D1\u548C\u7EF4\u62A4\u3002Linux \u5185\u6838\u7684\u5F00\u6E90\u6027\u8D28\u610F\u5473\u7740\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u67E5\u770B\u3001\u4FEE\u6539\u548C\u5206\u53D1\u5B83\u7684\u6E90\u4EE3\u7801\uFF0C\u8FD9\u4F7F\u5F97 Linux \u6210\u4E3A\u4E00\u4E2A\u975E\u5E38\u7075\u6D3B\u3001\u53EF\u5B9A\u5236\u548C\u5F3A\u5927\u7684\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><blockquote><p>\u5B8C\u6574\u7684 Linux \u64CD\u4F5C\u7CFB\u7EDF\u901A\u5E38\u88AB\u79F0\u4E3A &quot;<strong>GNU/Linux</strong>&quot;\uFF0C\u8FD9\u662F\u4E00\u4E2A\u66F4\u51C6\u786E\u548C\u5168\u9762\u7684\u79F0\u547C\u3002GNU/Linux \u5305\u62EC\u4E86 Linux \u5185\u6838\u4EE5\u53CA GNU \u9879\u76EE\u7684\u5DE5\u5177\u548C\u8F6F\u4EF6\uFF0C\u4EE5\u5F62\u6210\u4E00\u4E2A\u5B8C\u6574\u7684\u64CD\u4F5C\u7CFB\u7EDF\u3002GNU \u9879\u76EE\u7684\u5DE5\u5177\u5305\u62EC shell\u3001\u6587\u672C\u7F16\u8F91\u5668\u3001\u7F16\u8BD1\u5668\u3001\u6587\u4EF6\u7BA1\u7406\u5668\u7B49\uFF0C\u5B83\u4EEC\u4E3A Linux \u64CD\u4F5C\u7CFB\u7EDF\u63D0\u4F9B\u4E86\u7528\u6237\u754C\u9762\u548C\u57FA\u672C\u7684\u7CFB\u7EDF\u5DE5\u5177\u3002</p></blockquote><h3 id="linux\u64CD\u4F5C\u7CFB\u7EDF\u7684\u7279\u70B9\u6709" tabindex="-1"><a class="header-anchor" href="#linux\u64CD\u4F5C\u7CFB\u7EDF\u7684\u7279\u70B9\u6709" aria-hidden="true">#</a> Linux\u64CD\u4F5C\u7CFB\u7EDF\u7684\u7279\u70B9\u6709\uFF1A</h3><ol><li><p>\u5F00\u6E90\uFF1ALinux \u5185\u6838\u4EE5\u53CA\u5927\u90E8\u5206\u4E0E\u4E4B\u5173\u8054\u7684\u8F6F\u4EF6\u90FD\u662F\u5F00\u6E90\u7684\uFF0C\u8FD9\u610F\u5473\u7740\u7528\u6237\u53EF\u4EE5\u81EA\u7531\u5730\u8BBF\u95EE\u3001\u4FEE\u6539\u548C\u5206\u53D1\u4EE3\u7801\u3002\u793E\u533A\u8D44\u6E90\u4E30\u5BCC\uFF0C\u751F\u6001\u597D\u3002</p></li><li><p>\u591A\u6837\u5316\u7684\u53D1\u884C\u7248\uFF1ALinux \u6709\u8BB8\u591A\u4E0D\u540C\u7684\u53D1\u884C\u7248\uFF08Distribution\uFF09\uFF0C\u6BCF\u4E2A\u53D1\u884C\u7248\u5728 Linux \u5185\u6838\u7684\u57FA\u7840\u4E0A\u6DFB\u52A0\u4E86\u4E0D\u540C\u7684\u8F6F\u4EF6\u5305\u548C\u5DE5\u5177\uFF0C\u4EE5\u6EE1\u8DB3\u4E0D\u540C\u7528\u6237\u9700\u6C42\u3002\u4E00\u4E9B\u77E5\u540D\u7684 Linux \u53D1\u884C\u7248\u5305\u62EC Ubuntu\u3001Fedora\u3001Debian\u3001CentOS \u7B49\u3002</p></li><li><p>\u9AD8\u6027\u80FD\u548C\u7A33\u5B9A\u6027\uFF1ALinux \u5728\u591A\u79CD\u786C\u4EF6\u5E73\u53F0\u4E0A\u8FD0\u884C\uFF0C\u5E76\u4E14\u5177\u6709\u51FA\u8272\u7684\u6027\u80FD\u548C\u53EF\u4F38\u7F29\u6027\u3002\u8FD9\u4F7F\u5F97\u5B83\u9002\u7528\u4E8E\u5404\u79CD\u4E0D\u540C\u7684\u670D\u52A1\u5668\u5DE5\u4F5C\u8D1F\u8F7D\uFF0C\u4ECEWeb\u670D\u52A1\u5668\u5230\u6570\u636E\u5E93\u670D\u52A1\u5668\u548C\u865A\u62DF\u5316\u670D\u52A1\u5668\u3002Linux\u7684\u670D\u52A1\u5668\u53D1\u884C\u7248\u4E13\u95E8\u8BBE\u8BA1\u7528\u4E8E\u5728\u670D\u52A1\u5668\u73AF\u5883\u4E2D\u63D0\u4F9B\u7A33\u5B9A\u6027\u3001\u6027\u80FD\u548C\u5B89\u5168\u6027\u3002</p></li><li><p>\u5B89\u5168\u6027\u9AD8\uFF1A\u7531\u4E8E\u5176\u5F00\u6E90\u6027\u8D28\uFF0CLinux \u53D7\u5230\u5E7F\u6CDB\u5BA1\u67E5\uFF0C\u4ECE\u800C\u53EF\u4EE5\u66F4\u5FEB\u5730\u53D1\u73B0\u548C\u4FEE\u590D\u6F5C\u5728\u7684\u5B89\u5168\u6F0F\u6D1E\u3002\u6B64\u5916\uFF0CLinux \u62E5\u6709\u5F3A\u5927\u7684\u6743\u9650\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u53EF\u4EE5\u5E2E\u52A9\u4FDD\u62A4\u670D\u52A1\u5668\u514D\u53D7\u672A\u7ECF\u6388\u6743\u7684\u8BBF\u95EE</p></li><li><p>\u53EF\u79FB\u690D\u6027\uFF1ALinux \u5185\u6838\u53EF\u4EE5\u5728\u5404\u79CD\u786C\u4EF6\u5E73\u53F0\u4E0A\u8FD0\u884C\uFF0C\u4ECE\u4E2A\u4EBA\u8BA1\u7B97\u673A\u5230\u5D4C\u5165\u5F0F\u7CFB\u7EDF\u548C\u8D85\u7EA7\u8BA1\u7B97\u673A\u3002</p></li></ol><h3 id="linux\u5E38\u7528\u547D\u4EE4" tabindex="-1"><a class="header-anchor" href="#linux\u5E38\u7528\u547D\u4EE4" aria-hidden="true">#</a> Linux\u5E38\u7528\u547D\u4EE4</h3><p><code>uname -a</code>\u663E\u793A\u7CFB\u7EDF\u4FE1\u606F\u3002</p><p><code>free -h</code>\u663E\u793A\u5185\u5B58\u4F7F\u7528\u60C5\u51B5\u3002</p><p><code>df -h</code>\u663E\u793A\u6587\u4EF6\u7CFB\u7EDF\u78C1\u76D8\u7A7A\u95F4\u4F7F\u7528\u60C5\u51B5\u3002</p><p><code>du -sh /path/to/directory</code>\uFF1A\u663E\u793A\u76EE\u5F55\u7684\u603B\u78C1\u76D8\u4F7F\u7528\u60C5\u51B5\u3002</p><p><code>du -h --max-depth=1 /path/to/directory</code>\uFF1A\u9650\u5236\u67E5\u770B\u76EE\u5F55\u7684\u6DF1\u5EA6\uFF0C\u5E76\u663E\u793A\u5404\u4E2A\u4E00\u7EA7\u5B50\u76EE\u5F55\u7684\u78C1\u76D8\u4F7F\u7528\u60C5\u51B5\u3002</p><p><code>du -h --max-depth=1 /path/to/directory | sort -h</code>\uFF1A\u6309\u78C1\u76D8\u4F7F\u7528\u60C5\u51B5\u6392\u5E8F\u5E76\u663E\u793A\u5404\u4E2A\u4E00\u7EA7\u5B50\u76EE\u5F55\u7684\u78C1\u76D8\u4F7F\u7528\u60C5\u51B5\u3002</p><p><code>lsof</code> \u662F\u4E00\u4E2A\u7528\u4E8E\u5217\u51FA\u6253\u5F00\u6587\u4EF6\uFF08Open Files\uFF09\u548C\u5957\u63A5\u5B57\uFF08Open Sockets\uFF09\u7684\u547D\u4EE4\u884C\u5DE5\u5177\uFF0C\u5B83\u7528\u4E8E\u663E\u793A\u5F53\u524D\u7CFB\u7EDF\u4E0A\u6B63\u5728\u4F7F\u7528\u7684\u6587\u4EF6\u3001\u76EE\u5F55\u548C\u7F51\u7EDC\u8FDE\u63A5\u7684\u4FE1\u606F\u3002</p><p><code>lsof /path/to/directory</code> \u663E\u793A\u76EE\u5F55\u4E0B\u88AB\u6253\u5F00\u7684\u6587\u4EF6\u3002</p><p><code>lsof /path/to/file</code> \u663E\u793A\u7279\u70B9\u6587\u4EF6\u7684\u6253\u5F00\u8005\u3002</p><p><code>lsof -i</code>\u663E\u793A\u7F51\u7EDC\u8FDE\u63A5\u3002</p><p><code>lsof -i -sTCP:LISTEN</code> \u663E\u793A\u76D1\u542C\u7684\u7F51\u7EDC\u8FDE\u63A5\u3002</p><p><code>lsof -i :80</code> \u663E\u793A\u54EA\u4E2A\u8FDB\u7A0B\u5728\u4F7F\u752880\u7AEF\u53E3\u3002</p><p><strong>\u8F6F\u4EF6\u5305\u7BA1\u7406</strong><code>apt update -y &amp;&amp; apt upgrade -y </code>\u81EA\u52A8\u66F4\u65B0\u7CFB\u7EDF\u7684\u8F6F\u4EF6\u5305\u548C\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u786E\u4FDD\u7CFB\u7EDF\u4E2D\u7684\u6240\u6709\u8F6F\u4EF6\u90FD\u662F\u6700\u65B0\u7684\u3002</p><p><strong>\u7F51\u7EDC\u5DE5\u5177</strong> ifconfig\uFF1A\u67E5\u770B\u548C\u914D\u7F6E\u7F51\u7EDC\u63A5\u53E3\u3002 netstat -tul \uFF1A\u67E5\u770B\u6240\u6709\u76D1\u542C\u7684\u7AEF\u53E3</p><ul><li><p><code>ss</code>\uFF08Socket Statistics\uFF09\u547D\u4EE4\u7528\u4E8E\u663E\u793A\u5957\u63A5\u5B57\u4FE1\u606F\uFF0C\u5305\u62EC\u7F51\u7EDC\u8FDE\u63A5\u3001\u8DEF\u7531\u8868\u3001\u63A5\u53E3\u7EDF\u8BA1\u7B49\u3002\u5B83\u662F\u4E00\u4E2A\u975E\u5E38\u5F3A\u5927\u7684\u5DE5\u5177\uFF0C\u7528\u4E8E\u7F51\u7EDC\u914D\u7F6E\u548C\u6545\u969C\u6392\u9664\u3002</p><p><code>ss -t/-u/-l/-r</code> \u67E5\u8BE2\u6240\u6709tcp\u8FDE\u63A5/udp\u8FDE\u63A5/\u76D1\u542C\u72B6\u6001/\u8DEF\u7531\u8868</p></li><li><p><code>ip</code> \u547D\u4EE4\u662F\u4E00\u4E2A\u7528\u4E8E\u7BA1\u7406\u7F51\u7EDC\u63A5\u53E3\u3001\u5730\u5740\u548C\u8DEF\u7531\u7684\u591A\u529F\u80FD\u5DE5\u5177\uFF0C\u5B83\u662F ifconfig \u548C route \u547D\u4EE4\u7684\u73B0\u4EE3\u66FF\u4EE3\u54C1\u3002</p><p><code>ip a</code> \u663E\u793A\u6240\u6709\u7F51\u7EDC\u63A5\u53E3 <code>ip route</code> \u663E\u793A\u8DEF\u7531\u8868\u4FE1\u606F</p></li></ul><p><strong>\u6743\u9650\u66F4\u6539 chmod</strong><code>chmod 770 file.txt</code>\u6539\u53D8\u6587\u4EF6\u6216\u8005\u76EE\u5F55\u6743\u9650\u3002</p><p>\u4E09\u4E2A\u6570\u5B57\u6309\u987A\u5E8F\u8868\u793A\uFF0C\u6587\u4EF6\u6240\u6709\u8005\u6587\u4EF6\u3001\u6240\u5C5E\u7EC4\u548C\u5176\u4ED6\u7528\u6237\u7684\u6743\u9650\u3002 \u6BCF\u4E2A\u6570\u5B57\u7531\u4E09\u4F4D\u4E8C\u8FDB\u5236\u6570\u5B57\u7EC4\u6210\uFF0C\u5206\u522B\u4EE3\u8868\u8BFB\uFF08r\uFF09\u3001\u5199\uFF08w\uFF09\u3001\u6267\u884C\uFF08x\uFF09\u6743\u9650\u3002</p><ul><li>r\uFF08\u8BFB\u6743\u9650\uFF09\u5BF9\u5E94\u4E8E\u6570\u5B57 4\u3002</li><li>w\uFF08\u5199\u6743\u9650\uFF09\u5BF9\u5E94\u4E8E\u6570\u5B57 2\u3002</li><li>x\uFF08\u6267\u884C\u6743\u9650\uFF09\u5BF9\u5E94\u4E8E\u6570\u5B57 1\u3002</li></ul><p>7\u4E3A\u6700\u9AD8\u6743\u9650\uFF0C0\u4E3A\u6CA1\u6709\u6743\u9650\u3002</p><p><strong>\u7BA1\u9053\u7B26\u53F7 |</strong></p><p>\u5C06\u201C | \u201D\u7B26\u53F7\u524D\u9762\u547D\u4EE4\u6267\u884C\u7684\u7ED3\u679C\u4F5C\u4E3A\u540E\u9762\u4E00\u6761\u547D\u4EE4\u7684\u6807\u51C6\u8F93\u5165</p><p><code>cat /etc/config|grep</code></p><p><strong>\u6587\u672C\u641C\u7D22\u5339\u914D grep</strong></p><p><code>grep [\u9009\u9879] \u6A21\u5F0F \u6587\u4EF6\u540D</code>\uFF0C\u901A\u5E38\u914D\u5408\u7BA1\u9053\u7B26\u53F7\u4F7F\u7528</p><p><strong>\u6587\u672C\u5904\u7406 awk</strong></p><p><code>awk &#39;pattern { action }&#39;</code></p><p>\u5176\u4E2D\uFF1A</p><ul><li><code>&#39;pattern&#39;</code> \u662F\u4E00\u4E2A\u6A21\u5F0F\uFF0C\u7528\u4E8E\u5339\u914D\u6587\u672C\u4E2D\u7684\u884C\u3002</li><li><code>{ action }</code> \u662F\u5728\u6A21\u5F0F\u5339\u914D\u7684\u884C\u4E0A\u6267\u884C\u7684\u52A8\u4F5C\u3002</li><li><code>input-file</code> \u662F\u8981\u5904\u7406\u7684\u8F93\u5165\u6587\u4EF6\u3002</li></ul><p><code>awk</code> \u4F1A\u9010\u884C\u8BFB\u53D6\u8F93\u5165\u6587\u4EF6\uFF0C\u7136\u540E\u6839\u636E\u6A21\u5F0F\u5339\u914D\u6765\u51B3\u5B9A\u662F\u5426\u6267\u884C\u76F8\u5E94\u7684\u52A8\u4F5C\u3002\u5982\u679C\u6A21\u5F0F\u5339\u914D\u6210\u529F\uFF0C\u5C31\u4F1A\u6267\u884C\u76F8\u5E94\u7684\u52A8\u4F5C\u3002\u5982\u679C\u6CA1\u6709\u63D0\u4F9B\u6A21\u5F0F\uFF0C\u5219\u4F1A\u9ED8\u8BA4\u5339\u914D\u6240\u6709\u884C\u3002</p><p><code>awk</code> \u7684\u6838\u5FC3\u601D\u60F3\u662F\u5C06\u6BCF\u4E00\u884C\u5206\u5272\u6210\u5B57\u6BB5\uFF0C\u7136\u540E\u53EF\u4EE5\u901A\u8FC7 <code>$1</code>\u3001<code>$2</code> \u7B49\u53D8\u91CF\u6765\u8BBF\u95EE\u5B57\u6BB5\u7684\u5185\u5BB9\uFF0C\u4EE5\u53CA\u4F7F\u7528\u5185\u7F6E\u51FD\u6570\u6765\u8FDB\u884C\u64CD\u4F5C\u3002</p><h2 id="windous\u5B50\u7CFB\u7EDFwsl" tabindex="-1"><a class="header-anchor" href="#windous\u5B50\u7CFB\u7EDFwsl" aria-hidden="true">#</a> windous\u5B50\u7CFB\u7EDFwsl</h2><h3 id="\u4EC0\u4E48\u662Fwsl" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662Fwsl" aria-hidden="true">#</a> \u4EC0\u4E48\u662Fwsl</h3><p>WSL (Windows Subsystem for Linux) \u662F\u4E00\u79CD\u7531\u5FAE\u8F6F\u5F00\u53D1\u7684\u6280\u672F\uFF0C\u5B83\u5141\u8BB8\u5728 Windows \u64CD\u4F5C\u7CFB\u7EDF\u4E0A\u8FD0\u884C Linux \u53D1\u884C\u7248\u3002WSL \u7684\u76EE\u6807\u662F\u63D0\u4F9B\u4E00\u4E2A\u5B8C\u6574\u7684 Linux \u5185\u6838\u548C\u7528\u6237\u7A7A\u95F4\u73AF\u5883\uFF0C\u4EE5\u4FBF\u5F00\u53D1\u4EBA\u5458\u53EF\u4EE5\u5728 Windows \u4E0A\u4F7F\u7528\u7C7B\u4F3C\u4E8E Linux \u7684\u547D\u4EE4\u884C\u5DE5\u5177\u548C\u5E94\u7528\u7A0B\u5E8F\uFF0C\u800C\u65E0\u9700\u53CC\u542F\u52A8\u6216\u865A\u62DF\u673A\u3002</p><h3 id="\u5B89\u88C5wsl" tabindex="-1"><a class="header-anchor" href="#\u5B89\u88C5wsl" aria-hidden="true">#</a> \u5B89\u88C5wsl</h3><ol><li>\u641C\u7D22\u201C\u529F\u80FD\u201D\uFF0C\u6253\u5F00\u201C\u542F\u7528\u6216\u5173\u95EDWindows\u529F\u80FD\u201D\uFF0C\u6216\u8005\u5728\u8BBE\u7F6E\uFF0C\u5E94\u7528\uFF0C\u53EF\u9009\u529F\u80FD\uFF0C\u62C9\u5230\u6700\u4E0B\u9762\u66F4\u591AWindows\u529F\u80FD\u3002</li><li>\u52FE\u9009\u4EE5\u4E0B\u529F\u80FD\u3002 <ul><li>\u865A\u62DF\u673A\u5E73\u53F0</li><li>\u9002\u7528\u4E8E Linux \u7684 Windows \u5B50\u7CFB\u7EDF</li></ul></li><li>\u7B49\u5230\u5B89\u88C5\u5B8C\u6210\u540E\u91CD\u65B0\u542F\u52A8\u7535\u8111\u3002</li><li>\u5B89\u88C5Linux\u53D1\u884C\u7248\uFF0C\u6253\u5F00\u5546\u5E97\u5B89\u88C5\u9700\u8981\u7684Linux\u53D1\u884C\u7248\uFF08\u5982 Ubuntu\u3001Debian\u3001Arch Linux \u7B49\uFF09\u3002</li><li>\u521D\u59CB\u5316 Linux \u53D1\u884C\u7248\uFF0C\u6253\u5F00\u5B89\u88C5\u7684Linux\u53D1\u884C\u7248\uFF0C\u521B\u5EFA\u7528\u6237\u540D\u548C\u5BC6\u7801\uFF0C\u7B49\u5230\u51FA\u73B0Linux\u7684\u547D\u4EE4\u884C\u63D0\u793A\u7B26\u5C31\u6210\u529F\u4E86\u3002</li></ol><blockquote><p>\u5E38\u89C1\u95EE\u9898\uFF1A</p><p>\u82E5\u51FA\u73B0Error\uFF1AWSL 2 ?????????????? https://aka.ms/wsl2Kernel\u3002\u8BF4\u660E\u662Fwsl\u5185\u6838\u6CA1\u66F4\u65B0\uFF0C\u6253\u5F00PowerShell\uFF0C\u8F93\u5165<code>wsl --update</code>\u66F4\u65B0wsl\u5185\u6838\uFF0C\u91CD\u542Fwsl <code>wsl --shutdown</code>\u5C31\u53EF\u4EE5\u4E86\u3002</p></blockquote><h3 id="wsl\u57FA\u672C\u547D\u4EE4" tabindex="-1"><a class="header-anchor" href="#wsl\u57FA\u672C\u547D\u4EE4" aria-hidden="true">#</a> WSL\u57FA\u672C\u547D\u4EE4</h3><blockquote><ul><li><code>wsl --list --online</code> \u67E5\u770B\u53EF\u901A\u8FC7\u5728\u7EBF\u5546\u5E97\u83B7\u5F97\u7684 Linux \u53D1\u884C\u7248\u5217\u8868\u3002 \u6B64\u547D\u4EE4\u4E5F\u53EF\u8F93\u5165\u4E3A\uFF1A<code>wsl -l -o</code>\u3002</li><li><code>wsl -l -v</code> \u67E5\u770B\u5B89\u88C5\u5728 Windows \u8BA1\u7B97\u673A\u4E0A\u7684 Linux \u53D1\u884C\u7248\u5217\u8868\uFF0C\u5305\u62EC\u72B6\u6001\u548C WSL \u7248\u672C\u3002</li><li><code>wsl --set-default &lt;Distribution Name&gt;</code> \u8BBE\u7F6E\u9ED8\u8BA4 Linux \u53D1\u884C\u7248\u3002</li><li><code>wsl --update</code>\u66F4\u65B0WSL\u3002</li><li><code>wsl --status</code>\u67E5\u770B\u6709\u5173 WSL \u914D\u7F6E\u7684\u5E38\u89C4\u4FE1\u606F\uFF0C\u4F8B\u5982\u9ED8\u8BA4\u53D1\u884C\u7248\u7C7B\u578B\u3001\u9ED8\u8BA4\u53D1\u884C\u7248\u548C\u5185\u6838\u7248\u672C\u3002</li></ul></blockquote><h3 id="\u914D\u5408vs-code\u4F7F\u7528" tabindex="-1"><a class="header-anchor" href="#\u914D\u5408vs-code\u4F7F\u7528" aria-hidden="true">#</a> \u914D\u5408VS Code\u4F7F\u7528</h3><ol><li><p>\u5728VScode\u4E2D\u5B89\u88C5WSL\u62D3\u5C55</p></li><li><p>\u4F7F\u7528<code>CTRL+SHIFT+P</code> \u8C03\u51FA\u547D\u4EE4\u9762\u677F\uFF0C\u8F93\u5165wsl\u9009\u62E9\u8FDE\u63A5\u5230wsl\uFF0C\u8FDE\u63A5\u6210\u529F\u540E\u5DE6\u4E0B\u89D2\u4F1A\u663E\u793ALinux\u53D1\u884C\u7248\u540D\u79F0\u3002\u6216\u8005\u76F4\u63A5\u5728wsl\u547D\u4EE4\u884C\u7684\u76EE\u5F55\u4E0B\u8F93\u5165<code>code .</code>\u76F4\u63A5\u5728vscode\u4E2D\u6253\u5F00\u8BE5\u6587\u4EF6\u5939\u3002</p></li></ol><h2 id="\u5728wsl\u4E2D\u5B89\u88C5miniconda" tabindex="-1"><a class="header-anchor" href="#\u5728wsl\u4E2D\u5B89\u88C5miniconda" aria-hidden="true">#</a> \u5728wsl\u4E2D\u5B89\u88C5miniconda</h2><h3 id="\u4EC0\u4E48\u662Fminiconda" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662Fminiconda" aria-hidden="true">#</a> \u4EC0\u4E48\u662FMiniconda</h3><p>Miniconda \u662F\u4E00\u4E2A\u8F7B\u91CF\u7EA7\u7684\u8F6F\u4EF6\u5305\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u7528\u4E8E\u7BA1\u7406\u548C\u90E8\u7F72 <strong>Python</strong> \u548C\u5176\u4ED6\u7F16\u7A0B\u8BED\u8A00\u7684\u8F6F\u4EF6\u5305\u548C\u73AF\u5883\u3002\u5B83\u662F Anaconda \u7684\u7CBE\u7B80\u7248\uFF0CAnaconda \u662F\u4E00\u4E2A\u66F4\u5927\u7684\u6570\u636E\u79D1\u5B66\u548C\u673A\u5668\u5B66\u4E60\u5DE5\u5177\u96C6\uFF0C\u5305\u62EC\u4E86\u5927\u91CF\u7684\u6570\u636E\u79D1\u5B66\u5E93\u548C\u5DE5\u5177\u3002Miniconda \u7684\u76EE\u6807\u662F\u63D0\u4F9B\u4E00\u4E2A\u66F4\u5C0F\u3001\u66F4\u7075\u6D3B\u7684\u5B89\u88C5\u9009\u9879\uFF0C\u5141\u8BB8\u7528\u6237\u81EA\u5DF1\u9009\u62E9\u8981\u5B89\u88C5\u7684\u8F6F\u4EF6\u5305\uFF0C\u800C\u4E0D\u662F\u50CF Anaconda \u90A3\u6837\u4E00\u6B21\u6027\u5B89\u88C5\u5927\u91CF\u7684\u8F6F\u4EF6\u5305\u3002</p><p>Miniconda \u5141\u8BB8\u7528\u6237\u521B\u5EFA\u548C\u7BA1\u7406\u72EC\u7ACB\u7684 Python \u73AF\u5883\uFF0C\u6BCF\u4E2A\u73AF\u5883\u90FD\u53EF\u4EE5\u6709\u81EA\u5DF1\u7684\u8F6F\u4EF6\u5305\u4F9D\u8D56\u5173\u7CFB\uFF0C\u4ECE\u800C\u9694\u79BB\u4E0D\u540C\u9879\u76EE\u7684\u4F9D\u8D56\u3002\u5BF9\u4E8E\u4E0D\u540C\u9879\u76EE\u9700\u8981\u4E0D\u540C\u7684Python\u73AF\u5883\uFF0C\u6216\u8005\u9700\u8981\u7EF4\u62A4\u4E00\u4E2A\u65E7\u7248\u672CPython\u7684\u9879\u76EE\u6765\u8BF4\u975E\u5E38\u597D\u7528\uFF0C\u76F8\u5F53\u4E8E\u4E00\u4E2A\u865A\u62DF\u73AF\u5883\u3002</p><h3 id="\u5728wsl\u7684ubuntu\u4E2D\u5B89\u88C5miniconda" tabindex="-1"><a class="header-anchor" href="#\u5728wsl\u7684ubuntu\u4E2D\u5B89\u88C5miniconda" aria-hidden="true">#</a> \u5728WSL\u7684ubuntu\u4E2D\u5B89\u88C5miniconda</h3><ol><li>\u5728<a href="https://docs.conda.io/projects/miniconda/en/latest/" target="_blank" rel="noopener noreferrer">Miniconda</a>\u5B98\u7F51\uFF0C\u9009\u62E9\u6700\u65B0\u7684miniconda\u7684Linux\u7248\u672C\u4E0B\u8F7D</li><li>\u5C06.sh\u6587\u4EF6\u590D\u5236\u5230ubuntu\u4E2D</li><li>\u6267\u884C<code>bash Miniconda3-latest-Linux-x86_64.sh</code>\u5B89\u88C5\u4E0B\u8F7D\u7684miniconda\u3002</li></ol><p>\u6216\u8005\u4F7F\u7528\u547D\u4EE4\u884C\u5FEB\u901F\u5B89\u88C5</p><blockquote><p>These four commands quickly and quietly install the latest 64-bit version of the installer and then clean up after themselves. To install a different version or architecture of Miniconda for Linux, change the name of the <code>.sh</code> installer in the <code>wget</code> command.</p><div class="language-text ext-text"><pre class="language-text"><code>mkdir -p ~/miniconda3
wget https://repo.anaconda.com/miniconda/Miniconda3-latest-Linux-x86_64.sh -O ~/miniconda3/miniconda.sh
bash ~/miniconda3/miniconda.sh -b -u -p ~/miniconda3
rm -rf ~/miniconda3/miniconda.sh
</code></pre></div><p>After installing, initialize your newly-installed Miniconda. The following commands initialize for bash and zsh shells:</p><div class="language-text ext-text"><pre class="language-text"><code>~/miniconda3/bin/conda init bash
~/miniconda3/bin/conda init zsh
</code></pre></div></blockquote><p>\u5B89\u88C5\u5B8C\u6210\u540E\u6253\u5F00\u65B0\u7684\u7EC8\u7AEF\u7A97\u53E3\uFF0C\u6267\u884Cconda\u6709\u51FA\u73B0conda\u547D\u4EE4\u8BF4\u660E\u5B89\u88C5\u6210\u529F\uFF0C\u8F93\u5165<code>conda config --set auto_activate_base false</code>\u53EF\u4EE5\u53BB\u6389\u547D\u4EE4\u884C\u524D\u9762\u7684\uFF08base\uFF09\uFF0C\u53D6\u6D88\u9ED8\u8BA4\u542F\u52A8base\u73AF\u5883\u3002</p><h4 id="\u914D\u7F6Econda\u56FD\u5185\u955C\u50CF" tabindex="-1"><a class="header-anchor" href="#\u914D\u7F6Econda\u56FD\u5185\u955C\u50CF" aria-hidden="true">#</a> \u914D\u7F6Econda\u56FD\u5185\u955C\u50CF</h4><p>\u8FD9\u91CC\u4EE5\u963F\u91CC\u4E91\u955C\u50CF\u4E3A\u4F8B\uFF0C\u5728\u963F\u91CC\u4E91\u955C\u50CF\u7F51\u7AD9\u641C\u7D22anaconda</p><ul><li><p>Linux\u7528\u6237\u53EF\u4EE5\u901A\u8FC7\u4FEE\u6539\u7528\u6237\u76EE\u5F55\u4E0B\u7684 <code>.condarc</code> \u6587\u4EF6\uFF0C\u6DFB\u52A0\u4E0B\u9762\u4EE3\u7801\u3002</p><div class="language-text ext-text"><pre class="language-text"><code>channels:
  - defaults
show_channel_urls: true
default_channels:
  - http://mirrors.aliyun.com/anaconda/pkgs/main
  - http://mirrors.aliyun.com/anaconda/pkgs/r
  - http://mirrors.aliyun.com/anaconda/pkgs/msys2
custom_channels:
  conda-forge: http://mirrors.aliyun.com/anaconda/cloud
  msys2: http://mirrors.aliyun.com/anaconda/cloud
  bioconda: http://mirrors.aliyun.com/anaconda/cloud
  menpo: http://mirrors.aliyun.com/anaconda/cloud
  pytorch: http://mirrors.aliyun.com/anaconda/cloud
  simpleitk: http://mirrors.aliyun.com/anaconda/cloud
</code></pre></div><p>\u5373\u53EF\u6DFB\u52A0 Anaconda Python \u514D\u8D39\u4ED3\u5E93\u3002</p><p>\u914D\u7F6E\u5B8C\u6210\u53EF\u8FD0\u884C <code>conda clean -i</code> \u6E05\u9664\u7D22\u5F15\u7F13\u5B58\u3002</p><h4 id="conda\u547D\u4EE4" tabindex="-1"><a class="header-anchor" href="#conda\u547D\u4EE4" aria-hidden="true">#</a> conda\u547D\u4EE4</h4><ul><li><code>conda env list</code>\u5217\u51FA\u6240\u6709\u7684conda\u73AF\u5883</li><li><code>conda create --name env_name python=3.10</code> \u521B\u5EFA\u540D\u4E3Aenv_name\u7684python3.10\u73AF\u5883</li><li><code>conda remove --name env_name --all</code>\u5220\u9664conda\u73AF\u5883env_name</li><li><code>conda update conda</code>\u66F4\u65B0conda\u5230\u6700\u65B0\u7248\u672C</li><li><code>conda activate env_name</code> \u6FC0\u6D3Bconda\u73AF\u5883</li><li><code>conda deactivate</code> \u9000\u51FAconda\u73AF\u5883</li></ul></li></ul><h2 id="\u5728ubuntu\u4E2D\u5B89\u88C5zsh" tabindex="-1"><a class="header-anchor" href="#\u5728ubuntu\u4E2D\u5B89\u88C5zsh" aria-hidden="true">#</a> \u5728ubuntu\u4E2D\u5B89\u88C5zsh</h2><h3 id="\u4EC0\u4E48\u662Fzsh" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662Fzsh" aria-hidden="true">#</a> \u4EC0\u4E48\u662Fzsh</h3><p><a href="https://www.zsh.org/" target="_blank" rel="noopener noreferrer">Zsh</a>\u662F\u4E00\u79CD\u547D\u4EE4\u884C\u58F3\uFF08shell\uFF09\uFF0C\u7528\u4E8E\u5728\u8BA1\u7B97\u673A\u4E0A\u4E0E\u64CD\u4F5C\u7CFB\u7EDF\u8FDB\u884C\u4EA4\u4E92\u3002\u5B83\u662F Bourne shell\uFF08sh\uFF09\u7684\u6269\u5C55\uFF0C\u63D0\u4F9B\u4E86\u8BB8\u591A\u589E\u5F3A\u7684\u529F\u80FD\u548C\u7528\u6237\u53CB\u597D\u7684\u547D\u4EE4\u884C\u4F53\u9A8C\u3002</p><p><strong>zsh</strong> \u662F\u4E00\u4E2A\u517C\u5BB9 bash \u7684 shell\uFF0C\u76F8\u8F83 bash \u5177\u6709\u4EE5\u4E0B\u4F18\u70B9\uFF1A</p><ul><li>Tab \u8865\u5168\u529F\u80FD\u5F3A\u5927\uFF1A\u547D\u4EE4\u3001\u547D\u4EE4\u53C2\u6570\u3001\u6587\u4EF6\u8DEF\u5F84\u5747\u53EF\u4EE5\u8865\u5168\u3002</li></ul><ul><li>\u63D2\u4EF6\u548C\u4E3B\u9898\u4E30\u5BCC\uFF1A\u5FEB\u901F\u8F93\u5165\u4EE5\u524D\u4F7F\u7528\u8FC7\u7684\u547D\u4EE4\u3001\u5FEB\u901F\u8DF3\u8F6C\u6587\u4EF6\u5939\u3001\u663E\u793A\u7CFB\u7EDF\u8D1F\u8F7D\u8FD9\u4E9B\u90FD\u53EF\u4EE5\u901A\u8FC7\u63D2\u4EF6\u5B9E\u73B0\u3002</li><li>\u53EF\u6269\u5C55\u6027\u9AD8\uFF1AZsh \u5141\u8BB8\u7528\u6237\u7F16\u5199\u81EA\u5B9A\u4E49\u51FD\u6570\u3001\u522B\u540D\u548C\u811A\u672C\uFF0C\u4EE5\u6EE1\u8DB3\u7279\u5B9A\u9700\u6C42\uFF0C\u4ECE\u800C\u5C06\u547D\u4EE4\u884C\u5DE5\u5177\u96C6\u6210\u5230\u65E5\u5E38\u5DE5\u4F5C\u6D41\u7A0B\u4E2D\u3002</li></ul><h3 id="\u5B89\u88C5zsh" tabindex="-1"><a class="header-anchor" href="#\u5B89\u88C5zsh" aria-hidden="true">#</a> \u5B89\u88C5zsh</h3><ul><li><p>\u5728ubuntu\u547D\u4EE4\u884C\u4E2D\u6267\u884C<code>sudo apt-get install zsh</code>\u5B89\u88C5zsh\u3002</p></li><li><p>\u8F93\u5165<code>cat /etc/shells</code> \u67E5\u770B\u7CFB\u7EDF\u53EF\u7528\u7684shell</p></li><li><p>\u4F7F\u7528 <code>chsh -s /bin/zsh</code> \u547D\u4EE4\u5C06 zsh \u8BBE\u7F6E\u4E3A\u7CFB\u7EDF\u9ED8\u8BA4 shell\u3002\u6253\u5F00\u4E00\u4E2A\u65B0\u7684\u7EC8\u7AEF\uFF0C\u5C31\u53EF\u4EE5\u5F00\u59CB\u4F7F\u7528 zsh \u4E86\u3002</p></li></ul><h4 id="\u5B89\u88C5oh-my-zsh" tabindex="-1"><a class="header-anchor" href="#\u5B89\u88C5oh-my-zsh" aria-hidden="true">#</a> \u5B89\u88C5oh-my-zsh</h4><p>\u4F7F\u7528 curl \u4E0B\u8F7D\u811A\u672C\u5E76\u5B89\u88C5\uFF1A</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">sh</span> -c <span class="token string">&quot;<span class="token variable"><span class="token variable">$(</span><span class="token function">curl</span> -fsSL https://raw.github.com/ohmyzsh/ohmyzsh/master/tools/install.sh<span class="token variable">)</span></span>&quot;</span>
</code></pre></div><p>\u6216\u8005\u4F7F\u7528 wget \u4E0B\u8F7D\u811A\u672C\u5E76\u5B89\u88C5\uFF1A</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">sh</span> -c <span class="token string">&quot;<span class="token variable"><span class="token variable">$(</span><span class="token function">wget</span> https://raw.github.com/ohmyzsh/ohmyzsh/master/tools/install.sh -O -<span class="token variable">)</span></span>&quot;</span>
</code></pre></div><p><strong>\u4F7F\u7528\u56FD\u5185gitee\u955C\u50CF\u5B89\u88C5</strong>\uFF1A</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">sh</span> -c <span class="token string">&quot;<span class="token variable"><span class="token variable">$(</span><span class="token function">wget</span> <span class="token function">wget</span> https://gitee.com/mirrors/oh-my-zsh/raw/master/tools/install.sh -O -<span class="token variable">)</span></span>&quot;</span>
</code></pre></div><h4 id="\u4FEE\u6539oh-my-zsh\u4E3B\u9898" tabindex="-1"><a class="header-anchor" href="#\u4FEE\u6539oh-my-zsh\u4E3B\u9898" aria-hidden="true">#</a> \u4FEE\u6539oh-my-zsh\u4E3B\u9898</h4><p>\u5728 https://github.com/ohmyzsh/ohmyzsh/wiki/Themes \u4E2D\u67E5\u770B\u5185\u7F6E\u7684\u4E3B\u9898\u6837\u5F0F\u548C\u5BF9\u5E94\u7684\u4E3B\u9898\u540D\uFF0C\u6240\u6709\u5F53\u524D\u4E3B\u9898\u90FD\u53EF\u4EE5\u5728<code>~/.oh-my-zsh/themes</code>\u76EE\u5F55\u4E2D\u627E\u5230\u3002</p><p>\u5982\u679C\u8981\u542F\u7528\u4E3B\u9898\uFF0C\u5728 <code>~/.zshrc</code> \u4E2D\u8BBE\u7F6E <code>ZSH_THEME=\u4E3B\u9898\u7684\u540D\u79F0</code>\uFF0C\u5982\u679C\u4E0D\u5E0C\u671B\u542F\u7528\u4EFB\u4F55\u4E3B\u9898\uFF0C\u53EA\u9700\u8BBE\u7F6E\u4E3A\uFF1A <code>ZSH_THEME=&quot;&quot;</code>\uFF0C\u4F7F\u7528robbyrussell\u4E3B\u9898 <code>ZSH_THEME=robbyrussell</code>\u3002</p><p>\u9664\u4E86\u5185\u7F6E\u4E3B\u9898\u5916\u8FD8\u80FD\u5176\u4ED6\u7B2C\u4E09\u65B9\u5F00\u6E90\u4E3B\u9898\uFF0C\u4E0B\u8F7D\u4E3B\u9898\u653E\u5230<code>~/.oh-my-zsh/themes</code>\u76EE\u5F55\u4E0B\u5C31\u884C\u3002</p><p>\u8FD9\u91CC\u63A8\u8350<a href="https://github.com/romkatv/powerlevel10k" target="_blank" rel="noopener noreferrer">powerlevel10k</a>\u4E3B\u9898:</p><blockquote><ol><li><p>Clone the repository:</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">git</span> clone --depth<span class="token operator">=</span><span class="token number">1</span> https://github.com/romkatv/powerlevel10k.git <span class="token variable">\${ZSH_CUSTOM<span class="token operator">:-</span>$HOME<span class="token operator">/</span>.oh-my-zsh<span class="token operator">/</span>custom}</span>/themes/powerlevel10k
</code></pre></div><p>Users in China can use the official mirror on gitee.com for faster download. \u4E2D\u56FD\u7528\u6237\u53EF\u4EE5\u4F7F\u7528 gitee.com \u4E0A\u7684\u5B98\u65B9\u955C\u50CF\u52A0\u901F\u4E0B\u8F7D.</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">git</span> clone --depth<span class="token operator">=</span><span class="token number">1</span> https://gitee.com/romkatv/powerlevel10k.git <span class="token variable">\${ZSH_CUSTOM<span class="token operator">:-</span>$HOME<span class="token operator">/</span>.oh-my-zsh<span class="token operator">/</span>custom}</span>/themes/powerlevel10k
</code></pre></div></li><li><p>Set <code>ZSH_THEME=&quot;powerlevel10k/powerlevel10k&quot;</code> in <code>~/.zshrc</code>.</p></li><li><p>\u7136\u540E\u6267\u884C <code>source ~/.zshrc</code> \u914D\u7F6E\u751F\u6548\uFF0C\u8FD9\u65F6\u4F1A\u63D0\u793A\u5BF9\u4E3B\u9898\u8FDB\u884C\u914D\u7F6E\uFF0C\u6309\u7167\u63D0\u793A\u8FDB\u884C\u5373\u53EF.</p></li></ol><p><em>\u5982\u679C\u60F3\u91CD\u65B0\u914D\u7F6E\u4E3B\u9898\uFF0C\u6267\u884C<code>p10k configure</code>\u91CD\u65B0\u5F15\u5BFC\u914D\u7F6E\u3002</em></p></blockquote><p>oh-my-zsh\u8FD8\u5185\u7F6E\u4E86\u5F88\u591A\u63D2\u4EF6\uFF0C\u5185\u7F6E\u63D2\u4EF6\u53EF\u4EE5\u5728 <code>~/.oh-my-zsh/plugins</code> \u4E2D\u67E5\u770B\uFF0C\u6216\u8005\u5728https://github.com/unixorn/awesome-zsh-plugins\u91CC\u9762\u67E5\u770B\u66F4\u591A\u63D2\u4EF6\u3002</p><h2 id="\u8BBE\u7F6E\u7F51\u7EDC\u4EE3\u7406" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u7F6E\u7F51\u7EDC\u4EE3\u7406" aria-hidden="true">#</a> \u8BBE\u7F6E\u7F51\u7EDC\u4EE3\u7406</h2><p>wsl\u548Cwindows\u662F\u4E24\u4E2A\u72EC\u7ACB\u7684\u7CFB\u7EDF\u73AF\u5883\uFF0C\u5B83\u4EEC\u662F\u5728\u540C\u4E00\u8BA1\u7B97\u673A\u4E0A\u7684\u4E0D\u540C\u7CFB\u7EDF\uFF0C\u6240\u4EE5wsl\u65E0\u6CD5\u76F4\u63A5\u4F7F\u7528\u7CFB\u7EDF\u4EE3\u7406\uFF0C\u53EF\u4EE5\u4F7F\u7528\u4EE3\u7406\u5DE5\u5177\u91CC\u7684\u5C40\u57DF\u7F51\u4EE3\u7406\u3002</p><ul><li>\u4EE3\u7406\u5DE5\u5177\u5F00\u542F\u5141\u8BB8\u5C40\u57DF\u7F51\u4EE3\u7406</li><li>wsl2\u7684windows\u7CFB\u7EDFip\u5730\u5740\u5B58\u5728/etc/resolv.conf \u6587\u4EF6\u4E2D\uFF0C\u8FD0\u884C\u4EE5\u4E0B\u547D\u4EE4\u6765\u83B7\u53D6\u4E3B\u673A\u7684 IP \u5730\u5740\uFF1A<code>cat /etc/resolv.conf</code>nameserver\u5C31\u662Fwindows\u7684ip\u5730\u5740\uFF0C\u6216\u8005\u6267\u884C<code>ip route</code>\u4E5F\u80FD\u770B\u5230\u3002</li></ul><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># \u8BBE\u7F6E\u4EE3\u7406</span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable">https_proxy</span><span class="token operator">=</span><span class="token string">&quot;http://<span class="token variable">\${hostip}</span>:<span class="token variable">\${http_hostport}</span>&quot;</span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable">http_proxy</span><span class="token operator">=</span><span class="token string">&quot;http://<span class="token variable">\${hostip}</span>:<span class="token variable">\${http_hostport}</span>&quot;</span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable">ALL_PROXY</span><span class="token operator">=</span><span class="token string">&quot;socks5://<span class="token variable">\${hostip}</span>:<span class="token variable">\${socks_hostport}</span>&quot;</span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable">all_proxy</span><span class="token operator">=</span><span class="token string">&quot;socks5://<span class="token variable">\${hostip}</span>:<span class="token variable">\${socks_hostport}</span>&quot;</span>
</code></pre></div><p>\u9A8C\u8BC1\u4EE3\u7406\u662F\u5426\u6210\u529F</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># \u4F7F\u7528\u4EE5\u4E0B\u547D\u4EE4\uFF0C\u82E5\u8FD4\u56DE\u4EE3\u7406\u670D\u52A1\u5668ip\u5730\u5740\u5219\u8868\u793A\u4EE3\u7406\u6210\u529F\uFF0C\u5982\u679C\u5361\u4F4F\u8868\u793A\u5931\u8D25</span>
<span class="token function">curl</span> https://api.ipify.org?format<span class="token operator">=</span>json
\u6216\u8005 
<span class="token function">curl</span> -v https://ip.jackjyq.com/
</code></pre></div><p>\u6DFB\u52A0\u81EA\u52A8\u8BBE\u7F6E\u811A\u672C\u5230 ~/.bashrc \u5E95\u90E8\uFF0C\u6267\u884C<code>vi ~/.bashrc</code>\uFF0C\u5728\u672B\u5C3E\u6DFB\u52A0\u8FD9\u6BB5\u4EE3\u7801\uFF1A</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># add proxy</span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable">hostip</span><span class="token operator">=</span><span class="token variable"><span class="token variable">$(</span><span class="token function">cat</span> /etc/resolv.conf<span class="token operator">|</span><span class="token function">grep</span> nameserver<span class="token operator">|</span><span class="token function">awk</span> <span class="token string">&#39;{print $2}&#39;</span><span class="token variable">)</span></span>
<span class="token builtin class-name">export</span> <span class="token assign-left variable">socks_hostport</span><span class="token operator">=</span>\u4EE3\u7406\u7AEF\u53E3
<span class="token builtin class-name">export</span> <span class="token assign-left variable">http_hostport</span><span class="token operator">=</span>\u4EE3\u7406\u7AEF\u53E3
<span class="token builtin class-name">alias</span> <span class="token assign-left variable">proxy</span><span class="token operator">=</span><span class="token string">&#39;
    export https_proxy=&quot;http://\${hostip}:\${http_hostport}&quot;
    export http_proxy=&quot;http://\${hostip}:\${http_hostport}&quot;
    export ALL_PROXY=&quot;socks5://\${hostip}:\${socks_hostport}&quot;
    export all_proxy=&quot;socks5://\${hostip}:\${socks_hostport}&quot;
&#39;</span>
<span class="token builtin class-name">alias</span> <span class="token assign-left variable">unproxy</span><span class="token operator">=</span><span class="token string">&#39;
    unset ALL_PROXY
    unset https_proxy
    unset http_proxy
    unset all_proxy
&#39;</span>
<span class="token builtin class-name">alias</span> <span class="token assign-left variable">echoproxy</span><span class="token operator">=</span><span class="token string">&#39;
    echo $ALL_PROXY
    echo $all_proxy
    echo $https_proxy
    echo $http_proxy
&#39;</span>
<span class="token comment">#end proxy</span>
</code></pre></div><p>\u7136\u540E\u6267\u884C<code>source ~/.bashrc</code>\u4F7F\u4FEE\u6539\u751F\u6548</p><p>\u6267\u884Cproxy\u8BBE\u7F6E\u4EE3\u7406\uFF0C\u6267\u884Cunproxy\u53D6\u6D88\u4EE3\u7406\uFF0C\u6267\u884Cechoproxy\u67E5\u770B\u4EE3\u7406\u3002</p><h2 id="ssh\u8FDC\u7A0B\u8FDE\u63A5" tabindex="-1"><a class="header-anchor" href="#ssh\u8FDC\u7A0B\u8FDE\u63A5" aria-hidden="true">#</a> SSH\u8FDC\u7A0B\u8FDE\u63A5</h2><p>SSH\uFF08Secure Shell\uFF09\u662F\u4E00\u79CD\u7F51\u7EDC\u534F\u8BAE\u548C\u52A0\u5BC6\u6280\u672F\uFF0C\u7528\u4E8E\u5B89\u5168\u5730\u8FDE\u63A5\u5230\u8FDC\u7A0B\u8BA1\u7B97\u673A\u6216\u670D\u52A1\u5668\uFF0C\u5E76\u5728\u5B89\u5168\u901A\u9053\u4E0A\u8FDB\u884C\u8FDC\u7A0B\u7BA1\u7406\u548C\u6570\u636E\u4F20\u8F93\u3002SSH \u65E8\u5728\u63D0\u4F9B\u6570\u636E\u4F20\u8F93\u7684\u673A\u5BC6\u6027\u548C\u8EAB\u4EFD\u9A8C\u8BC1\uFF0C\u4EE5\u786E\u4FDD\u901A\u4FE1\u7684\u9690\u79C1\u548C\u5B8C\u6574\u6027\u3002</p><p>SSH \u5177\u6709\u4EE5\u4E0B\u4E3B\u8981\u529F\u80FD\u548C\u7279\u70B9\uFF1A</p><ul><li>\u52A0\u5BC6\u901A\u4FE1\uFF1A\u4FDD\u62A4\u6570\u636E\u4F20\u8F93\u7684\u9690\u79C1\u548C\u5B8C\u6574\u6027\uFF0C\u9632\u6B62\u672A\u7ECF\u6388\u6743\u7684\u8BBF\u95EE\u8005\u62E6\u622A\u6216\u7A83\u542C\u901A\u4FE1\u5185\u5BB9\u3002</li><li>\u8EAB\u4EFD\u9A8C\u8BC1\uFF1A\u8981\u6C42\u7528\u6237\u63D0\u4F9B\u8EAB\u4EFD\u9A8C\u8BC1\u51ED\u8BC1\uFF0C\u901A\u5E38\u662F\u7528\u6237\u540D\u548C\u5BC6\u7801\u6216\u516C\u94A5/\u79C1\u94A5\uFF0C\u4EE5\u786E\u8BA4\u5176\u5408\u6CD5\u6027\u3002</li><li>\u8FDC\u7A0B\u7BA1\u7406\uFF1A\u5141\u8BB8\u8FDC\u7A0B\u7BA1\u7406\u670D\u52A1\u5668\uFF0C\u6267\u884C\u547D\u4EE4\u3001\u4F20\u8F93\u6587\u4EF6\u548C\u8BBE\u7F6E\u914D\u7F6E\u3002</li><li>\u7AEF\u53E3\u8F6C\u53D1\uFF1A\u652F\u6301\u521B\u5EFA\u52A0\u5BC6\u96A7\u9053\uFF0C\u4F7F\u8FDC\u7A0B\u670D\u52A1\u5668\u4E0A\u7684\u670D\u52A1\u53EF\u4EE5\u901A\u8FC7\u672C\u5730\u8BA1\u7B97\u673A\u8BBF\u95EE\u3002</li><li>\u591A\u79CD\u5B9E\u73B0\uFF1A\u6709\u591A\u79CD SSH \u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u7684\u5B9E\u73B0\uFF0C\u5982OpenSSH\u3001Terminal\u3001MobaXterm\u3001PuTTY\u3001XShell\u3001FinalShell\u7B49\u7B49\u3002</li></ul><h3 id="\u4F7F\u7528vscode\u8FDC\u7A0B\u8FDE\u63A5linux\u670D\u52A1\u5668" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u7528vscode\u8FDC\u7A0B\u8FDE\u63A5linux\u670D\u52A1\u5668" aria-hidden="true">#</a> \u4F7F\u7528VScode\u8FDC\u7A0B\u8FDE\u63A5Linux\u670D\u52A1\u5668</h3><p>\u53EF\u4EE5\u53C2\u8003\u4E00\u4E0B\u5B98\u65B9\u6587\u7AE0\uFF1A<a href="https://code.visualstudio.com/blogs/2019/10/03/remote-ssh-tips-and-tricks" target="_blank" rel="noopener noreferrer">Remote SSH: Tips and Tricks</a></p><h4 id="\u5B89\u88C5remote-ssh\u63D2\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u5B89\u88C5remote-ssh\u63D2\u4EF6" aria-hidden="true">#</a> \u5B89\u88C5Remote-SSH\u63D2\u4EF6</h4><p>\u5B89\u88C5\u540E\uFF0C\u5728vscode\u5DE6\u4FA7\u8FB9\u680F\u4F1A\u5FC3\u810F\u4E00\u4E2A\u7535\u8111\u663E\u793A\u5668\u7684\u56FE\u6807\uFF0C\u9009\u62E9SSH\u65C1\u8FB9\u8BBE\u7F6E\uFF0C\u7F16\u8F91config\u6587\u4EF6\u8FDB\u884C\u8FDC\u7A0B\u8FDE\u63A5\u76F8\u5173\u8BBE\u7F6E\u3002 \u5185\u5BB9\u5982\u4E0B\uFF1A</p><div class="language-text ext-text"><pre class="language-text"><code>Host \u4E3B\u673A\u540D\u79F0 
  HostName \u670D\u52A1\u5668ip\u5730\u5740
  User \u767B\u5F55\u7684\u7528\u6237\u540D
  Port SSH\u7AEF\u53E3
 
Host mylinux
  HostName 192.168.1.102
  User root
  Port 22
</code></pre></div><p>\u914D\u7F6E\u5B8C\u6210\u540E\u9009\u62E9\u670D\u52A1\u5668\u8FDE\u63A5\uFF0C\u7B2C\u4E00\u6B21\u8FDE\u63A5\u9700\u8981\u5B89\u88C5\u670D\u52A1\u6BD4\u8F83\u6162\uFF0C\u9700\u8981\u7B49\u5F85\u4E00\u4E0B\uFF0C\u8FDE\u63A5\u6210\u529F\u540E\u5DE6\u4E0B\u4F1A\u663E\u793A\u4E3B\u673A\u540D\u3002</p><h4 id="proxycommand" tabindex="-1"><a class="header-anchor" href="#proxycommand" aria-hidden="true">#</a> ProxyCommand</h4><p>\u5BF9\u4E8E\u8FDC\u7A0B\u8BBF\u95EE\u7684vps\u5728\u56FD\u5916\u7684\u8BDD\uFF0C\u76F4\u63A5\u8FDE\u63A5\u53EF\u80FD\u5F88\u6162\uFF0Cssh\u8FD8\u5BB9\u6613\u65AD\u8FDE\uFF0C\u53EF\u4EE5\u9009\u62E9\u6DFB\u52A0\u4EE3\u7406\u3002 \u53EA\u9700\u8981\u5728ssh\u914D\u7F6E\u540E\u9762\u6DFB\u52A0<code>ProxyCommand connect.exe -H 127.0.0.1:10809 %h %p</code>,connect.exe\u670D\u52A1\u6765\u6E90\u4E8EGit\uFF0C\u9700\u8981\u63D0\u524D\u5B89\u88C5\u3002127.0.0.1:10809\u662F\u672C\u5730\u4EE3\u7406\u3002</p><div class="language-text ext-text"><pre class="language-text"><code>Host vps
  HostName ip\u5730\u5740
  User root
  Port 22
  ProxyCommand D:\\Git\\mingw64\\bin\\connect.exe -H 127.0.0.1:10809 %h %p
</code></pre></div><p>\u6216\u8005\u4F60\u7684vscode\u5DF2\u7ECF\u914D\u7F6E\u4E86\u5168\u5C40\u4EE3\u7406\u4E86\uFF0C\u5C31\u4E0D\u9700\u8981\u989D\u5916\u914D\u7F6EProxyCommand\u4E86\u3002 \u6309<code>F1</code>\u6216\u8005<code>shift+ctrl+p</code>\u641C\u7D22\u9ED8\u8BA4\u8BBE\u7F6E\uFF0C\u6253\u5F00\u9ED8\u8BA4\u8BBE\u7F6E(JSON),\u5728vscode\u8BBE\u7F6E\u7684\u914D\u7F6E\u6587\u4EF6\u6DFB\u52A0\uFF1A</p><div class="language-text ext-text"><pre class="language-text"><code>&quot;http.proxy&quot;: &quot;http://your-proxy-server:port&quot;,
&quot;https.proxy&quot;: &quot;https://your-proxy-server:port&quot;
</code></pre></div><p>\u8BBE\u7F6E\u672C\u5730\u4EE3\u7406\u3002</p><h4 id="\u5728\u8FDC\u7A0Bvps\u4E0A\u6DFB\u52A0\u672C\u5730\u516C\u94A5" tabindex="-1"><a class="header-anchor" href="#\u5728\u8FDC\u7A0Bvps\u4E0A\u6DFB\u52A0\u672C\u5730\u516C\u94A5" aria-hidden="true">#</a> \u5728\u8FDC\u7A0BVPS\u4E0A\u6DFB\u52A0\u672C\u5730\u516C\u94A5</h4><p>\u4E0D\u5EFA\u8BAE\u7528\u7528\u6237\u540D\u548C\u5BC6\u7801\u767B\u5F55vps\uFF0C\u76F4\u63A5\u8BA9\u8FDC\u7A0B\u7684vps\u4FE1\u4EFB\u672C\u5730\u7684\u4E3B\u673A\u5C31\u884C\u3002 windows\u7528\u6237\u5728\u7EC8\u7AEF\u4E2D\u8F93\u5165<code>ssh-keygen -t ed25519 -C &quot;your_email@example.com&quot;</code>\u751F\u6210ed25519\u7F16\u7801\u7684ssh\u516C\u94A5\u79C1\u94A5\uFF0C\u540E\u9762\u7684\u90AE\u7BB1\u53EF\u4EE5\u4E0D\u7528\u3002</p><blockquote><p>\u6CE8\u610F\uFF1A\u5982\u679C\u4F60\u4F7F\u7528\u7684\u662F\u4E0D\u652F\u6301 Ed25519 \u7B97\u6CD5\u7684\u65E7\u7CFB\u7EDF\uFF0C\u8BF7\u4F7F\u7528\u4EE5\u4E0B\u547D\u4EE4\uFF1A<code>ssh-keygen -t rsa -b 4096 -C &quot;your_email@example.com&quot;</code></p></blockquote><p>\u6210\u529F\u540E\u5728\u7528\u6237\u6587\u4EF6\u5939,ssh\u4E2D\u4F1A\u6709\u4E24\u4E2A\u6587\u4EF6\uFF0C<code>id_ed25519</code>\u79C1\u94A5\u548C<code>id_ed25519.pub</code>\u516C\u94A5\uFF0C\u5C06.ssh\u6587\u4EF6\u5939\u4E2D\u7684id_ed25519.pub\u516C\u94A5\u7684\u5185\u5BB9</p><blockquote><p>\u4E00\u822C\u957F\u8FD9\u6837:ssh-ed25519 ABCDEFGKSAfjksjafkjsaLJfakjJ...</p></blockquote><p>\u590D\u5236\u5230vps\u7684<code>/home/.ssh</code>\u6587\u4EF6\u5939\u7684<code>authorized_keys</code>\u6587\u4EF6\u4E2D\u3002\u5982\u679C\u6CA1\u6709.ssh\u6587\u4EF6\u5939\u548Cauthorized_keys\u6587\u4EF6\u53EF\u4EE5\u81EA\u5DF1\u521B\u5EFA\uFF0C\u7136\u540E\u6267\u884C<code>sudo service sshd restart</code>\u91CD\u542Fssh\u670D\u52A1\u3002</p><p>\u8FD9\u6837\u672C\u673A\u5C31\u80FD\u4E4B\u95F4\u8FDE\u63A5\u8FDC\u7A0B\u670D\u52A1\u5668\u4E0D\u9700\u8981\u5BC6\u7801\u4E86\uFF0C \u547D\u4EE4\u884C\u8F93\u5165<code>ssh -p 22 root@hostname</code>\u76F4\u63A5ssh\u8FDE\u63A5vps\u670D\u52A1\u5668\u3002</p><h3 id="\u91CA\u653Ewsl2\u7684\u786C\u76D8\u7A7A\u95F4" tabindex="-1"><a class="header-anchor" href="#\u91CA\u653Ewsl2\u7684\u786C\u76D8\u7A7A\u95F4" aria-hidden="true">#</a> \u91CA\u653EWSL2\u7684\u786C\u76D8\u7A7A\u95F4</h3><p>WSL2 \u4F7F\u7528\u865A\u62DF\u786C\u76D8 (VHD) \u6765\u5B58\u50A8 Linux \u6587\u4EF6\uFF0C\u5B83\u4F1A\u81EA\u52A8\u6269\u5C55\uFF0C\u4F46\u4E0D\u4F1A\u81EA\u52A8\u6536\u7F29\u3002\u6240\u4EE5\uFF0C\u5373\u4F7F\u4F60\u5220\u9664\u4E86Linux\u91CC\u9762\u7684\u6587\u4EF6\uFF0CVHD \u7684\u5927\u5C0F\u4E5F\u4E0D\u4F1A\u53D8\u5C0F\u3002\u6240\u4EE5\u8981\u5982\u4F55\u91CA\u653E\u6CA1\u7528\u5230\u7684\u78C1\u76D8\u5462\u3002</p><blockquote><p>\u5173\u4E8EWSL\u78C1\u76D8\u7A7A\u95F4\u95EE\u9898\uFF0C\u53EF\u4EE5\u53C2\u8003\u5FAE\u8F6F\u5B98\u65B9\u8FD9\u7BC7\u6587\u7AE0<a href="https://learn.microsoft.com/zh-cn/windows/wsl/disk-space" target="_blank" rel="noopener noreferrer">\u5982\u4F55\u7BA1\u7406 WSL \u78C1\u76D8\u7A7A\u95F4</a>\u3002</p></blockquote><ul><li>WSL2\u7684\u78C1\u76D8\u4E00\u822C\u6302\u8F7D\u5728<code>C:\\Users\\User\\AppData\\Local\\Packages\\CanonicalGroupLimited.UbuntuonWindows_79rhkp1fndgsc\\LocalState\\ext4.vhdx</code>\u3002</li><li>\u9996\u5148\uFF0C\u4F7F\u7528 <code>wsl.exe --shutdown</code> \u547D\u4EE4\u7EC8\u6B62\u6240\u6709WSL\u5B9E\u4F8B\u3002</li><li>\u7136\u540E\u5728\u7BA1\u7406\u5458\u6A21\u5F0F\u4E0B\u6253\u5F00 Windows \u547D\u4EE4\u63D0\u793A\u7B26\uFF0C\u8F93\u5165 <code>diskpart</code> \u547D\u4EE4\u3002\u5728 <code>DISKPART&gt;</code> \u63D0\u793A\u7B26\u4E0B\uFF0C\u8F93\u5165 <code>select vdisk file=&quot;&lt;pathToVHD&gt;&quot;</code>\u9009\u62E9\u865A\u62DF\u78C1\u76D8\u6587\u4EF6\uFF0C\u5176\u4E2D <code>&lt;pathToVHD&gt;</code> \u662F\u521A\u521A\u627E\u7684vhd\u78C1\u76D8\u8DEF\u5F84\u3002</li><li>\u4F7F\u7528<code>detail vdisk</code> \u67E5\u770B VHD \u7684\u5F53\u524D\u5927\u5C0F\u548C\u53EF\u7528\u7A7A\u95F4\u3002</li><li>\u538B\u7F29\u6587\u4EF6 <code>compact vdisk</code>\u3002</li><li>\u538B\u7F29\u5B8C\u6BD5\u540E\u5378\u8F7D\u78C1\u76D8 <code>detach vdisk</code>\u3002</li></ul>`,119),c=[t];function i(l,p){return a(),s("div",null,c)}var r=e(o,[["render",i],["__file","index.html.vue"]]);export{r as default};
